'use strict';

(function () {
    'use strict';

    config.$inject = ["$locationProvider", "$stateProvider", "$urlRouterProvider", "$httpProvider"];
    run.$inject = ["$rootScope", "$timeout", "$location", "$window"];
    angular.module('app', ['ui.router', 'ngMessages']).config(config).run(run);

    function config($locationProvider, $stateProvider, $urlRouterProvider, $httpProvider) {
        // set variable to control behaviour on initial app load
        window.initialLoad = true;

        $locationProvider.html5Mode(true);

        // default route
        $urlRouterProvider.otherwise("/");

        $stateProvider.state('home', {
            url: '/?:page',
            templateUrl: function templateUrl(stateParams) {
                return window.initialLoad ? null : '/?xhr=1' + (stateParams.page ? '&page=' + stateParams.page : '');
            }
        }).state('post-details', {
            url: '/post/:year/:month/:day/:slug',
            templateUrl: function templateUrl(stateParams) {
                return window.initialLoad ? null : '/post/' + stateParams.year + '/' + stateParams.month + '/' + stateParams.day + '/' + stateParams.slug + '?xhr=1';
            }
        }).state('posts-for-tag', {
            url: '/posts/tag/:tag',
            templateUrl: function templateUrl(stateParams) {
                return window.initialLoad ? null : '/posts/tag/' + stateParams.tag + '?xhr=1';
            }
        }).state('posts-for-month', {
            url: '/posts/:year/:month',
            templateUrl: function templateUrl(stateParams) {
                return window.initialLoad ? null : '/posts/' + stateParams.year + '/' + stateParams.month + '?xhr=1';
            }
        }).state('page-details', {
            url: '/page/:slug',
            templateUrl: function templateUrl(stateParams) {
                return window.initialLoad ? null : '/page/' + stateParams.slug + '?xhr=1';
            }
        }).state('archive', {
            url: '/archive',
            templateUrl: '/archive?xhr=1'
        }).state('contact', {
            url: '/contact',
            templateUrl: '/contact?xhr=1',
            controller: 'Contact.IndexController',
            controllerAs: 'vm'
        }).state('contact-thanks', {
            url: '/contact-thanks',
            templateUrl: '/contact-thanks?xhr=1'
        });

        // mark all requests from angular as ajax requests
        $httpProvider.defaults.headers.common['X-Requested-With'] = 'XMLHttpRequest';
    }

    function run($rootScope, $timeout, $location, $window) {
        // initialise google analytics
        $window.ga && $window.ga('create', 'UA-30211492-1', 'auto');

        $rootScope.$on('$stateChangeSuccess', function () {
            // hide mobile nav
            $rootScope.showNav = false;

            // track pageview
            var urlWithoutHash = $location.url().split('#')[0];
            $window.ga && $window.ga('send', 'pageview', urlWithoutHash);

            // jump to top of page if not initial page load
            if (!window.initialLoad) {
                document.body.scrollTop = document.documentElement.scrollTop = 0;
            }

            $timeout(function () {
                // run syntax highlighter plugin
                SyntaxHighlighter.highlight();
            });

            window.initialLoad = false;
        });
    }
})();
'use strict';

(function () {
    'use strict';

    Controller.$inject = ["$location", "ContactService"];
    angular.module('app').controller('Contact.IndexController', Controller);

    function Controller($location, ContactService) {
        var vm = this;

        vm.submit = submit;

        initController();

        function initController() {};

        function submit() {
            vm.error = null;
            vm.loading = true;
            ContactService.Send(vm).then(function () {
                $location.path('/contact-thanks');
            }).catch(function (error) {
                vm.error = 'Error: ' + error;
            }).finally(function () {
                vm.loading = false;
            });
        };
    }
})();
'use strict';

(function () {
    'use strict';

    Service.$inject = ["$http", "$q"];
    angular.module('app').factory('ContactService', Service);

    function Service($http, $q) {
        var service = {};

        service.Send = Send;

        return service;

        function Send(form) {
            return $http.post('/api/contact', form).then(handleSuccess, handleError);
        }

        // private functions

        function handleSuccess(res) {
            return res.data;
        }

        function handleError(res) {
            return $q.reject(res.data);
        }
    }
})();